import React, { useState } from 'react'; // Import useState for handling state

import b from '../../Assets/b.png';
import c from '../../Assets/c.png';
import d from '../../Assets/d.png';
import e from '../../Assets/e.png';

const Slider = () => {
  // State to track the current slide index
  const [currentSlide, setCurrentSlide] = useState(0);

  // Array to hold slide images
  const slides = [b, c, d, e];

  // Function to handle next slide
  const nextSlide = () => {
    const nextIndex = (currentSlide + 1) % slides.length;
    setCurrentSlide(nextIndex);
  };

  // Function to handle previous slide
  const prevSlide = () => {
    const prevIndex = (currentSlide - 1 + slides.length) % slides.length;
    setCurrentSlide(prevIndex);
  };

  return (
    <div id="indicators-carousel" className="relative w-full" data-carousel="static">
      {/* Carousel wrapper */}
      <div className="relative h-56 rounded-lg md:h-96">
        {/* Render each slide */}
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`duration-700 ease-in-out${currentSlide === index ? ' opacity-100' : ' opacity-0'}`}
            data-carousel-item
          >
            <img
              src={slide}
              className="absolute block w-full -translate-x-1/2 -translate-y-1/2 top-1/2 left-1/2"
              alt={`Slide ${index + 1}`}
            />
          </div>
        ))}
      </div>
      {/* Slider controls */}
      <button type="button" className="absolute top-0 start-0 z-30 flex items-center justify-center h-full px-4 cursor-pointer group focus:outline-none text-black" onClick={prevSlide} data-carousel-prev>
        {/* Previous button SVG */}
      </button>
      <button type="button" className="absolute top-0 end-0 z-30 flex items-center justify-center h-full px-4 cursor-pointer group focus:outline-none" onClick={nextSlide} data-carousel-next>
        {/* Next button SVG */}
      </button>
    </div>
  );
};

export default Slider;
