import React, { useState } from 'react';

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <>
      <div className="sm:hidden">
        <button
          onClick={toggleMenu}
          type="button"
          className="block text-gray-500 hover:text-white focus:text-white focus:outline-none"
        >
          <svg className="h-6 w-6 fill-current" viewBox="0 0 24 24">
            {menuOpen ? (
              <path
                fillRule="evenodd"
                clipRule="evenodd"
                d="M4 6h16v2H4V6zm0 5h16v2H4v-2zm16 5H4v2h16v-2z"
              />
            ) : (
              <path
                fillRule="evenodd"
                clipRule="evenodd"
                d="M4 6h16v2H4V6zm0 5h16v2H4v-2zm0 5h16v2H4v-2z"
              />
            )}
          </svg>
        </button>
      </div>

      <div className={`grid ${menuOpen ? 'block' : 'hidden'} grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mt-6 grid-cols-6 sm:grid-cols-2 md:grid-cols-3  sm:flex sm:items-center`}>
        <div className="bg-red-900">01</div>
        <div className="bg-yellow-900">02</div>
        <div className="bg-green-800">03</div>
      </div>
    </>
  );
};

export default Header;
